#!/bin/bash

# 安全测试脚本 - 测试C语言和C++版本的计算器安全性

echo "==========================================="
echo "科学计算器安全测试"
echo "==========================================="

# 导入共享测试工具函数库
SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
source "$SCRIPT_DIR/../common/test_utils.sh"

# 检查是否安装了Valgrind
check_valgrind() {
    if ! command -v valgrind &> /dev/null; then
        echo -e "${YELLOW}警告: 未找到 Valgrind，跳过内存检查测试${NC}"
        return 1
    fi
    return 0
}

# 安全测试函数
run_security_test() {
    local test_name="$1"
    local input="$2"
    local lang_version="$3"
    local test_type="$4"  # "memory" 或 "input"
    
    # 根据语言版本选择可执行文件
    if [ "$lang_version" = "c" ]; then
        exe_path="../../calculator_c/build_cmake/scientific_calculator"
    else
        exe_path="../../calculator_cpp/build_cmake/scientific_calculator"
    fi
    
    # 检查可执行文件是否存在
    if [ ! -f "$exe_path" ]; then
        echo -e "${RED}错误: 找不到可执行文件 $exe_path${NC}"
        return 1
    fi
    
    echo "正在测试 '$test_name' ($lang_version)..."
    
    if [ "$test_type" = "memory" ] && check_valgrind; then
        # 使用Valgrind进行内存检查
        temp_input="/tmp/calculator_security_input.txt"
        echo "$input" > "$temp_input"
        echo "quit" >> "$temp_input"
        
        # 运行Valgrind检查
        valgrind_output=$(timeout 30s valgrind --leak-check=full --error-exitcode=1 $exe_path < "$temp_input" 2>&1)
        valgrind_result=$?
        
        # 清理临时文件
        rm -f "$temp_input"
        
        if [ $valgrind_result -eq 0 ]; then
            echo -e "${GREEN}  内存检查通过${NC}"
            return 0
        else
            echo -e "${RED}  发现内存问题:${NC}"
            echo "$valgrind_output" | grep -E "(ERROR|definitely lost|indirectly lost)" | head -5
            return 1
        fi
    elif [ "$test_type" = "input" ]; then
        # 输入验证测试
        temp_input="/tmp/calculator_security_input.txt"
        echo "$input" > "$temp_input"
        echo "quit" >> "$temp_input"
        
        # 运行输入测试
        output=$(timeout 5s $exe_path < "$temp_input" 2>&1)
        test_result=$?
        
        # 清理临时文件
        rm -f "$temp_input"
        
        # 检查是否崩溃或异常退出
        if [ $test_result -eq 124 ]; then
            echo -e "${RED}  测试超时${NC}"
            return 1
        elif [ $test_result -ne 0 ] && [ $test_result -ne 124 ]; then
            echo -e "${RED}  程序异常退出 (退出码: $test_result)${NC}"
            return 1
        else
            # 检查输出中是否包含错误信息
            if echo "$output" | grep -qiE "(segmentation fault|core dumped|abort|assert)"; then
                echo -e "${RED}  检测到程序崩溃${NC}"
                return 1
            else
                echo -e "${GREEN}  输入处理正常${NC}"
                return 0
            fi
        fi
    else
        echo -e "${YELLOW}  跳过测试${NC}"
        return 0
    fi
}

# 构建项目函数
build_project_wrapper() {
    local lang_version="$1"
    build_project "$lang_version" "$SCRIPT_DIR"
}

# 测试C语言版本
echo ""
echo "-------------------------------------------"
echo "安全测试 - C语言版本"
echo "-------------------------------------------"

# 构建C语言版本
build_project_wrapper "c"

# 内存安全测试
echo -e "${BLUE}内存安全测试:${NC}"
run_security_test "缓冲区溢出测试" "......" "c" "input"
run_security_test "空输入测试" "" "c" "input"

# 输入验证测试
echo -e "${BLUE}输入验证测试:${NC}"
run_security_test "无效字符测试" "2 @ 3" "c" "input"
run_security_test "不匹配括号测试" "(2 + 3" "c" "input"
run_security_test "连续操作符测试" "2 + + 3" "c" "input"
run_security_test "除零测试" "5 / 0" "c" "input"
run_security_test "负数开方测试" "sqrt(-1)" "c" "input"
run_security_test "对数负数测试" "log(-1)" "c" "input"
run_security_test "超长表达式测试" "......" "c" "input"
run_security_test "特殊字符测试" "2 $ 3" "c" "input"
run_security_test "命令注入测试" "2 + 3; rm -rf /" "c" "input"

# 测试C++版本
echo ""
echo "-------------------------------------------"
echo "安全测试 - C++语言版本"
echo "-------------------------------------------"

# 构建C++语言版本
build_project_wrapper "cpp"

# 内存安全测试
echo -e "${BLUE}内存安全测试:${NC}"
run_security_test "缓冲区溢出测试" "......" "cpp" "input"
run_security_test "空输入测试" "" "cpp" "input"

# 输入验证测试
echo -e "${BLUE}输入验证测试:${NC}"
run_security_test "无效字符测试" "2 @ 3" "cpp" "input"
run_security_test "不匹配括号测试" "(2 + 3" "cpp" "input"
run_security_test "连续操作符测试" "2 + + 3" "cpp" "input"
run_security_test "除零测试" "5 / 0" "cpp" "input"
run_security_test "负数开方测试" "sqrt(-1)" "cpp" "input"
run_security_test "对数负数测试" "log(-1)" "cpp" "input"
run_security_test "超长表达式测试" "......" "cpp" "input"
run_security_test "特殊字符测试" "2 $ 3" "cpp" "input"
run_security_test "命令注入测试" "2 + 3; rm -rf /" "cpp" "input"

echo ""
echo "==========================================="
echo "安全测试完成"
echo "==========================================="
